mapper.select:

select member_id                        
from sg_member                           
where member_name=#{MEMBER_NAME_FIND}   
and member_email=#{MEMBER_EMAIL_FIND}


select member_password                 
from sg_member                           
where member_id = #{MEMBER_ID_FIND}   
and member_name=#{MEMBER_NAME_FIND}    
and member_email=#{MEMBER_EMAIL_FIND}
